import { Component, OnInit, Input } from '@angular/core';
import { Character } from '../characterObj';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss']
})
export class CardComponent implements OnInit {

  @Input() character: Character = new Character();

  constructor() { }

  ngOnInit(): void {
  }

}
<div class="rounded border">
  <img class="img-fluid m-auto d-block w-100" src={{character.image}} alt="character.name">
  <div class="my-3 ms-2 fs-3">{{character.name}}</div>
  <a class="btn btn-warning d-block w-75 m-auto my-2"> See More</a>
</div>
